 import React from "react";
 import {View, Text,SafeAreaView,StyleSheet} from "react-native";
 import COLORS from "./consts/colors";
 import { AntDesign } from '@expo/vector-icons';
 import { TextInput } from "react-native-gesture-handler";
 import { MaterialIcons } from '@expo/vector-icons';


 const Home= () => {
     const categories = ['HAND BAGS', 'SNEAKERS', 'HEELS','CLOTHES'];

     const CategoryList = ()=>{
         return<View style={style.categoryContainer}>
             
         </View>
     }
     return (
        <SafeAreaView
        style={{
            flex: 1,
            paddingHorizontal: 20,
            backgroundColor: COLORS.white,
            
        }}>
            <View style={style.header}>
                <View>
                    <Text style={{fontSize: 25, fontWeight: 'bold'}}>Welcome to </Text>
                    <Text style={{fontSize: 38, fontWeight: 'bold', color: COLORS.green}}>Gifty closet </Text>
                </View>
                <AntDesign name="shoppingcart" size={28} color="black" />
                <View>
                <View style={{marginTop: 30, flexDirection: 'row'}}>
                    <View style={style.searchContainer}>
                    
                <AntDesign name="search1" size={25} color="black" style={{marginLeft: 20}}/>  
                    <TextInput placeholder="search"/>
                    </View>
                    <View style={style.sortBtn}>
                    <MaterialIcons name="sort" size={30} color="black" />

                    </View>
                    </View>    

            </View >
            </View>
            <CategoryList/>

        </SafeAreaView>
    
    
         
     );
 };
 const style=StyleSheet.create({
     header:{
         marginTop: 20,
         flexDirection: 'row',
         justifyContent: 'space-between',
     },
     searchContainer:{
         height: 50,
         backgroundColor: COLORS.light,
         borderRadius: 10,
         flex: 1,
         flexDirection: 'row',
         alignItems: 'center',

     },
     input:{
         fontSize: 18,
         fontWeight: 'bold',
         color: COLORS.dark,
         flex: 1,
     },
    sortBtn:{
        marginLeft: 10,
        height: 50,
        width: 50,
        backgroundColor: COLORS.green,
        justifyContent:'center',
        alignItems: 'center',
        borderRadius: 10,
    },
    categoryContainer:{
        flexDirection: 'row',
        marginTop: 30,
        marginBottom: 20,
        justifyContent: 'space-between',

    },

 })   
 export default Home;
