 import React from "react";
 import {View, Text,SafeAreaView,StyleSheet,TextInput,TouchableOpacity} from "react-native";
 
 import { AntDesign } from '@expo/vector-icons';
 import COLORS from '../assets/consts/colors';
 import { MaterialIcons } from '@expo/vector-icons';
import { FlatList } from "react-native-gesture-handler";
import  items from "../assets/consts/items";


 const Home= () => {
     const categories = ['HANDBAGS', 'SNEAKERS', 'HEELS','CLOTHES'];

     const [categoryIndex,setCategoryIndex] = React.useState(0)

     const CategoryList = ()=>{
         return(
            <View style={style.categoryContainer}>
            {categories.map((items,index)=>(
                <TouchableOpacity key={index}>
                    
                    <Text
                     
                    style={[style.categoryText,
                    categoryIndex ==  index && style.categoryTextSelected]}>
           {items}
           </Text> 
                </TouchableOpacity>
                
            ))}
        </View>
         );
         
     };
     return (
        <SafeAreaView
        style={{
            flex: 1,
            paddingHorizontal: 20,
            backgroundColor: COLORS.white,
            
        }}>
            <View style={style.header}>
                <View>
                    <Text style={{fontSize: 25, fontWeight: 'bold'}}>Welcome to </Text>
                    <Text style={{fontSize: 38, fontWeight: 'bold'}}>Gifty closet </Text>
                </View>
                <AntDesign name="shoppingcart" size={28} color="black" />
                <View>
                <View style={{marginTop: 30, flexDirection: 'row'}}>
                    <View style={style.searchContainer}>
                    
                <AntDesign name="search1" size={25} color="black" style={{marginLeft: 20}}/>  
                    <TextInput placeholder="search"/>
                    </View>
                    <View style={style.sortBtn}>
                    <MaterialIcons name="sort" size={30} color="black" />

                    </View>
                    </View>    

            </View >
            </View>
            <CategoryList/>
            <FlatList numColumns={2} data={items}/>  

        </SafeAreaView>
    
    
         
     );
 };
 const style=StyleSheet.create({
     header:{
         marginTop: 20,
         flexDirection: 'row',
         justifyContent: 'space-between',
     },
     searchContainer:{
         height: 50,
         backgroundColor: COLORS.white,
         borderRadius: 10,
         flex: 1,
         flexDirection: 'row',
         alignItems: 'center',

     },
     input:{
         fontSize: 18,
         fontWeight: 'bold',
         color: COLORS.dark,
         flex: 1,
     },
    sortBtn:{
        marginLeft: 10,
        height: 50,
        width: 50,
        backgroundColor: COLORS.yellow,
        justifyContent:'center',
        alignItems: 'center',
        borderRadius: 10,
    },
    categoryContainer:{
        flexDirection: 'row',
        marginTop: 30,
        marginBottom: 20,
        justifyContent: 'space-between',

    },
    categoryText: {
        fontSize: 16,
        color:"grey",
        fontWeight: 'bold',
        
    },
    categoryTextSelected: {
        color: COLORS.yellow,
        paddingButtom: 5,
        borderButtomWidth: 2,
        borderColor: COLORS.yellow,
    },

 })   
 export default Home;
