 import React from "react";
 import {View, Text,SafeAreaView,StyleSheet,TextInput,TouchableOpacity, Dimensions} from "react-native";
 
 import { AntDesign } from '@expo/vector-icons';
 import COLORS from '../assets/consts/colors';
 import { MaterialIcons } from '@expo/vector-icons';
import { FlatList } from "react-native-gesture-handler";

import  items from "../assets/consts/items";
import { HeaderStyleInterpolators } from "@react-navigation/stack";


const width =Dimensions.get("screen").width/2-30

 const Home= () => {
     const categories = ['HANDBAGS', 'SNEAKERS', 'HEELS','CLOTHES'];

     const [categoryIndex,setCategoryIndex] = React.useState(0)

     const CategoryList = ()=>{
         return(
            <View style={style.categoryContainer}>
            {categories.map((items,index)=>(
                <TouchableOpacity key={index}>
                    
                    <Text
                     
                    style={[style.categoryText,
                    categoryIndex ==  index && style.categoryTextSelected]}>
           {items}
           </Text> 
                </TouchableOpacity>
                
            ))}
        </View>
         );
         
     };

     const card =({items}) => {
         return (
             <View style={style.card}>
                 <View style={{alignItems: 'flex-end' }}>
                     <View style={{width:30, 
                        height:30,
                         borderRadius: 15,
                         justifyContent: 'center',
                         backgroundColor: items.like
                         ? 'rgba(245, 42, 42,0.2)'
                         : 'rgba(0,0,0,0.2)'

                         }}>
                         
                         <MaterialIcons name="favorite-border" size={18} color="black"
                          color={items.like? COLORS.red: COLORS.dark} />  
                     </View>
                     <View style={{height:100, alignItems: 'center'}}>
                         <Image source={Heels.img}/>

                     </View>
                 
                 </View>

             </View>
         )
     }
     




     return (
        <SafeAreaView
        style={{
            flex: 1,
            paddingHorizontal: 20,
            backgroundColor: COLORS.white,
            
        }}>
            <View style={style.header}>
                <View>
                    <Text style={{fontSize: 25, fontWeight: 'bold'}}>Welcome to </Text>
                    <Text style={{fontSize: 38, fontWeight: 'bold'}}>Gifty closet </Text>
                </View>
                <AntDesign name="shoppingcart" size={28} color="black" />
                <View>
                <View style={{marginTop: 30, flexDirection: 'row'}}>
                <View style={style.searchContainer}>
                    <AntDesign name="search1" size={25} color="black" style={{marginLeft: 20}}/>  
                    <TextInput placeholder="search"/>
                </View>
                    <View style={style.sortBtn}> 
                    <MaterialIcons name="sort" size={30} color="black" />

                    </View>
                    </View>    

            </View >
            </View>
            <CategoryList/>
            <FlatList
            
             numColumns={2} 
             data={items} 
             renderItem={({item}) => <Card items={item} /> }/>

        </SafeAreaView>
    
    
         
     );
 };
 const style=StyleSheet.create({
     header:{
         marginTop: 20,
         flexDirection: 'row',
         justifyContent: 'space-between',
     },
     searchContainer:{
         height: 50,
         backgroundColor: COLORS.light,
         borderRadius: 10,
         flex: 1,
         flexDirection: 'row',
         alignItems: 'center',

     },
     input:{
         fontSize: 18,
         fontWeight: 'bold',
         color: COLORS.dark,
         flex: 1,
     },
    sortBtn:{
        marginLeft: 10,
        height: 50,
        width: 50,
        backgroundColor: COLORS.yellow,
        justifyContent:'center',
        alignItems: 'center',
        borderRadius: 10,
    },
    categoryContainer:{
        flexDirection: 'row',
        marginTop: 30,
        marginBottom: 20,
        justifyContent: 'space-between',

    },
    categoryText: {
        fontSize: 16,
        color:"grey",
        fontWeight: 'bold',
        
    },
    categoryTextSelected: {
        color: COLORS.yellow,
        paddingBottom: 5,
        borderBottomWidth: 2,
        borderColor: COLORS.yellow,
    },
    card:{
        height: 225,
        backgroundColor:COLORS.yellow,
        width,

        marginHorizontal:2,
        borderRadius: 10,
        marginBottom:20,
        padding: 15,
    },

 })   
 export default Home;
